    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        String name=jTextField3.getText();
        String pass=jPasswordField1.getText();
        String fe_name = "" , fe_pass = "";
        f = new JFrame();
        try
        {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/cat_project","root","");
            //Statement stmt = con.createStatement();
            String s1="select * from admin ";
            PreparedStatement ps=con.prepareStatement(s1);
            ResultSet rs = ps.executeQuery();
            while(rs.next())
            {
                if(rs.getString(1).equals(name))
                {                    
                    fe_name=rs.getString(1);
                    fe_pass=rs.getString(2);
                }
            }
            if(fe_name.equals(name) && fe_pass.equals(pass))
            {
                Adminpage a=new Adminpage();
                a.setVisible(true);
                dispose();
            }
            else
            {
                Dialog d = new Dialog(f,"Dialog Box");
                Label l = new Label("Invaild Username and Password");
                d.add(l);
                d.setSize(200,200);
                d.setVisible(true);
                
            }
            con.close();
        }
        catch(Exception e)
        {
            System.out.println(e);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

