    private void jTextField3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jTextField3ActionPerformed
        // TODO add your handling code here:
        String Username = jTextField3.getText();
        String Password = jPasswordField1.getText();
        flag=0;
        try{
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/cat_project","root","");
            PreparedStatement st = (PreparedStatement)con.prepareStatement("select Username, Password where Username=? and Password=?");
            st.setString(1,Username);
            st.setString(2,Password);
            ResultSet rs = st.executeQuery();
             
            while(rs.next()){
               // System.out.println(rs.getString(1)+" "+rs.getString(2));
                if((jTextField3.getText()).equals(rs.getString(1))){
                    if((jPasswordField1.getText()).equals(rs.getString(2))){
                        Adminpage A=new Adminpage();
                        A.setVisible(true);}
                }                
            }
            con.close();
        }
        catch(Exception e){System.out.println(e);} 
    }//GEN-LAST:event_jTextField3ActionPerformed

